class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long lakshay = 0;

        unordered_map<long long, long long> pichla;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> abhi;

            if (nums[i] == k) {
                lakshay++;
            }
            abhi[nums[i]]++;

            for (auto it = pichla.begin(); it != pichla.end(); ++it) {
                long long it1 = it->first;
                long long it2 = it->second;
                long long chlra = it1 & nums[i];
                if (chlra == k) {
                    lakshay += it2;
                }
                abhi[chlra] += it2;
            }

            pichla = abhi;
        }

        return lakshay;
    }
};
