class Solution {
public:
    long long f(vector<int>&nums,int k){
        vector<int>bits(32);
        long long ans=0;
        int i=0,j=0,n=nums.size(),andVal=INT_MAX;
        while(j<n){
            andVal&=nums[j];
            for(int digit=0;digit<31;digit++){
                if(nums[j]&(1<<digit)) bits[digit]++;
            }
            j++;
            while(i<j && andVal<k){
                for(int digit=0;digit<31;digit++){
                    if(nums[i]&(1<<digit)) bits[digit]--;
                    if(bits[digit]==j-i-1) andVal|=(1<<digit);
                }
                // cout<<i<<' '<<j<<" "<<andVal<<endl;
                i++;
            }
            if(andVal>=k &&i<=j) ans+=j-i;
        }
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        long long x=f(nums,k),y=f(nums,k+1);
        // cout<<x<<" "<<y;
        return x-y;
    }
};
