class Solution {
public:
    long long countSubarrays(vector<int>& a, int b) {
        int c = a.size();
        long long d = 0;

        unordered_map<long long, long long> e;

        for (int f = 0; f < c; ++f) {
            d += processElement(a, b, f, e);
        }

        return d;
    }

private:
    long long processElement(vector<int>& a, int b, int f, unordered_map<long long, long long>& e) {
        unordered_map<long long, long long> g;
        long long d = 0;

        if (a[f] == b) {
            ++d;
        }
        g[a[f]] = 1;

        for (auto& [h, i] : e) {
            long long j = computeAndResult(h, a[f]);
            if (j == b) {
                d += i;
            }
            g[j] += i;
        }

        e = g;
        return d;
    }

    long long computeAndResult(long long h, int a_f) {
        return h & a_f;
    }
};
