#define ll long long int
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll cnt=0;
        unordered_map<ll,ll> mp;
        for (int i=0;i<nums.size();i++) {
            unordered_map<ll,ll> mp2;
            ll ta=k; 
            if (nums[i]==ta) cnt++;
            mp2[nums[i]]=1;
            for (auto it:mp) {
                ll res= it.first & nums[i];
                if (res==ta) cnt+=it.second;
                mp2[res]+=it.second;
            }
            mp=mp2;
        }
        return cnt;

    }
};
