class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long int ct=0;
        int n=nums.size();
        map<long long int,long long int>m1;
        for(int i=0;i<n;i++){
            map<long long int, long long int>m2;
            long long int andd=nums[i];
            if(andd==k){
                ct++;
            }
            m2[nums[i]]=1;
            for(auto it: m1){
                andd=it.first & nums[i];
                if(andd==k){
                    ct+=it.second;
                }
                m2[andd]+=it.second;
            }
            m1=m2;
        }
        return ct;
    }
};
