class Solution {
public:
     long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long c = 0;

        unordered_map<long long, long long> p;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> c2; 

            if (nums[i] == k) {
                ++c;
            }
            c2[nums[i]] = 1;

            for (auto& [v, f] : p) {
                long long n2 = v & nums[i];
                if (n2 == k) {
                    c += f;
                }
                c2[n2] += f;
            }

            p = c2;
        }

        return c;
    }
};
