from collections import defaultdict
class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        prev = defaultdict(int)
        for num in nums:
            curr = defaultdict(int) 
            if num == k:
                count += 1
            curr[num] = 1
            for val, freq in prev.items():
                new_and_result = val & num
                if new_and_result == k:
                    count += freq
                curr[new_and_result] += freq
            prev = curr
        return count
