class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        unordered_map<long long, long long> result1;
        for(int i=0;i<nums.size();i++){
            if(k==nums[i])
                ans++;
        }
        for(int i=0;i<nums.size();i++)
        {
            unordered_map<long long, long long> result2;
            result2[nums[i]]=1;
            for(auto & [a,b] : result1)
            {
                int and1= a&nums[i];
                if(and1 == k)
                    ans+=b;
                result2[and1]+=b;
            }
            result1=result2;
        }
    return ans;
    }
};
