class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long , long long> prev;//value , freq
        for(int i = 0 ; i<n ; i++)
        {
            unordered_map<long long, long long> curr;
            if(nums[i] == k)
            {
                ans++;
            }
            curr[nums[i]] = 1;
            for(auto it : prev)
            {
                long long newAnd = (it.first & nums[i]);
                if(newAnd == k)
                {
                    ans += it.second;
                }
                curr[newAnd] += it.second;
            }
            prev = curr;
        }

        return ans;
    }
};
