class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        unordered_map<long long,long long> prev;
         int n = nums.size();
        long long ans = 0;
        for (int i = 0; i <n;i++) { 
            if (nums[i] == k) ans++;
            unordered_map<long long, long long> curr; 
            curr[nums[i]] = 1;
            for (auto it : prev) {
                long long temp = it.first & nums[i];
                if (temp == k) {
                    ans += it.second;
                }
                curr[temp] +=it.second;
            }
            prev = curr;
        }
        return ans;
    }
};
