class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int s = nums.size();
        long long total = 0;
        unordered_map<long long, long long> previousR;
        for(int index = 0; index<s;index++){
            unordered_map<long long, long long> currentR;
            if(nums[index] == k){
                total++;
            }
            currentR[nums[index]] = 1;
            for(auto &[value, frequency]: previousR){
                long long newAndResult = value & nums[index];
                if(newAndResult == k){
                    total += frequency;
                }
                currentR[newAndResult] += frequency;
            }

            previousR = currentR;
        }
        return total;
    }
};
