class Solution:
    def countSubarrays(self, arr: List[int], x: int) -> int:
        n = len(arr)
        c = 0
        p = defaultdict(int)

        for i in range(n):
            d = defaultdict(int)

            if arr[i] == x:
                c += 1

            d[arr[i]] = 1

            for v, f in p.items():
                n = v & arr[i]

                if n == x:
                    c += f
                d[n] += f

            p = d

        return c
