class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int vimala=nums.size();
        long long c=0;
        unordered_map<long long,long long>vimalares;
        for(int i=0;i<vimala;i++){
                 unordered_map<long long,long long>currentres;
            if(nums[i]==k)
                c++;
            currentres[nums[i]]=1;
           for(auto&[x,y]:vimalares){
               long long nc=x&nums[i];
               if(nc==k){
                   c+=y;
               }
               currentres[nc]+=y;
           }
            vimalares=currentres;
        }
        return c;
    }
};
