class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int n = array.size();
        long long res = 0;
        unordered_map<long long, long long> prev_res;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 

            if (array[i] == target) {
                ++res;
            }

            curr[array[i]] = 1;

            for (auto& [val, f] : prev_res) {
                long long new_r = val & array[i];

                if (new_r == target) {
                    res += f;
                }

                curr[new_r] += f;
            }

            prev_res = curr;
        }

        return res;
    }
};
