# define ll long long
class Solution {
public:
     ll countSubarrays(vector<int>& arr, int k) {
        int size = arr.size();
        ll cnt = 0;
        unordered_map<ll, ll> pre;
        for (int p = 0; p < size; ++p) {
            unordered_map<ll, ll> curr; 
            if (arr[p] == k) {
                ++cnt;
            }
            curr[arr[p]] = 1;
            for (auto& [i, j] : pre) {
                ll nar = i & arr[p];
                if (nar == k) {
                    cnt += j;
                }
                curr[nar] += j;
            }

            pre = curr;
        }
        return cnt;
    }
};
