from typing import List
from collections import defaultdict

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        t = 0
        pr = defaultdict(int)

        for i in range(n):
            cr = defaultdict(int)
            if nums[i] == k:
                t += 1
            cr[nums[i]] = 1
            for v, f in pr.items():
                nar = v & nums[i]
                if nar == k:
                    t += f
                cr[nar] += f

            pr = cr

        return t
