class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res = 0;
        unordered_map<int, int> map;
        int N = nums.size();

        for(int i = 0; i < N; i++) {
            unordered_map<int, int> nextmap;
            nextmap[nums[i]]++;
            if(nums[i] == k) {
                res++;
            }
            for(auto [num,times] : map) {
                if((num & nums[i]) == k) {
                    res+= times;
                }
                nextmap[num & nums[i]] += times;
            }


            map = nextmap;
        }
        return res;
    }
};


