class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n=nums.size();
        unordered_map<long long ,long long> prev;
        for(int i=0;i<n;i++){
            unordered_map<long long ,long long> cur;
            if(nums[i]==k){
                ans++;
            }
            cur[nums[i]]=1;
            for(auto j:prev){
                if((j.first&nums[i])==k){
                    ans+=j.second;
                }
                cur[(j.first&nums[i])]+=j.second;
            }
            prev=cur;
        }
        return ans;
    }
};
