class Solution:
    def countSubarrays(self, nums, k):
        n = len(nums)
        count = 0

        prev_results = {}

        for i in range(n):
            curr_results = {}

            if nums[i] == k:
                count += 1
            curr_results[nums[i]] = 1

            for val, freq in prev_results.items():
                new_and_result = val & nums[i]
                if new_and_result == k:
                    count += freq
                if new_and_result in curr_results:
                    curr_results[new_and_result] += freq
                else:
                    curr_results[new_and_result] = freq

            prev_results = curr_results

        return count

# Example usage:
sol = Solution()
nums1 = [1, 2, 3, 4]
k1 = 1
print(sol.countSubarrays(nums1, k1))  # Adjust example values as needed

