#define ll long long
class Solution {
private:
    ll solve(vector<int>&nums, int k){
        int size = nums.size();
        ll ans = 0;
        unordered_map<ll, ll> prev;
        for (int i = 0; i < size; i++) {
            unordered_map<ll, ll> temp; 
            if (nums[i] == k) {
                ans++;
            }
            temp[nums[i]] = 1;
            for (auto it : prev){
                int value = it.first;
                int freq = it.second;
                ll res = value & (nums[i]);
                if (res == k) {
                    ans += freq;
                }
                temp[res] += freq;
            }

            prev = temp;
        }
        return ans;
    }
public:
    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums,k);
    }
};
