class Solution:
    def countSubarrays(self, array: List[int], target: int) -> int:
        total = 0
        previous_results = {}
        for num in array:
            current_results = {}
            if num == target:
                total += 1
            current_results[num] = 1
            for value, frequency in previous_results.items():
                new_and_result = value & num
                if new_and_result == target:
                    total += frequency
                current_results[new_and_result] = current_results.get(new_and_result, 0) + frequency
            previous_results = current_results
        return total
