class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         int n = nums.size();
        long long no = 0;

        unordered_map<long long, long long>m;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> map; 

            if (nums[i] == k) {
                no++;
            }
            map[nums[i]] = 1;

            for (auto& ch : m) {
                int entry=ch.first;
                int freq= ch.second;

                long long ans = entry & nums[i];
                if (ans == k) {
                    no += freq;
                }
               map[ans] += freq;
            }

           m = map;
        }

        return no;
    }
};
