class Solution {
    public:
        long long countSubarrays(vector<int>& nums, int k) {
            long long n = nums.size();
            long long result = 0;
            vector<int> raju;
            unordered_map<long long, long long> piche;

            for (long long i = 0; i < nums.size(); i++) {
                unordered_map<long long, long long> aage; 

                if (nums[i] == k) {
                    result++;
                }
                aage[nums[i]] = 1;

                for (auto& [val, freq] : piche) {
                    long long count =   (val & nums[i]);
                    long long x = count;
                    if (x == k) {
                        result = result + freq;
                    }
                    aage[x] = aage[x] + freq;
                }

                piche = aage;
            }
            return result;
        }
    };
