class Solution {
public:
    long long countSubarrays(vector<int>& numbers, int target) {
        int size = numbers.size();
        long long count = 0;

        unordered_map<long long, long long> res;

        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> curr; 

            if (numbers[i] == target) {
                ++count;
            }
            curr[numbers[i]] = 1;

            for (auto& [value, frequency] : res) {
                long long newAndResult = value & numbers[i];
                if (newAndResult == target) {
                    count += frequency;
                }
                curr[newAndResult] += frequency;
            }

            res = curr;
        }

        return count;
    }
};
