class Solution {
public:
    long long countSubarrays(vector<int>& array, int k) {
        int size = array.size();
        long long ans = 0;
        unordered_map<long long, long long> Pr;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> Cr; 
            if (array[index] == k) {
                ++ans;
            }
            Cr[array[index]] = 1;
            for (auto& [value, frequency] : Pr) {
                long long newAndResult = value & array[index];
                if (newAndResult == k) {
                    ans += frequency;
                }
                Cr[newAndResult] += frequency;
            }

            Pr = Cr;
        }
        return ans;
    }
};
