class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();

        unordered_map<long long, long long> prev;
        long long cnt = 0;
        for(int i = 0; i<n; i++){
            unordered_map<long long, long long> curr;
            if(nums[i]==k) cnt++;
            curr[nums[i]]=1;
            for(auto& [val, freq]: prev){
                long long news = val&nums[i];
                if(news==k) cnt+=freq;
                curr[news]+=freq;
            }
            prev=curr;
        }
        return cnt;
    }
};
