class Solution:

    def countSubarrays(self, nums: List[int], k: int) -> int:
        a, b = defaultdict(int), defaultdict(int)

        ans = 0
        for i in nums:
            b.clear()
            if i == k:
                ans += 1

            for key, value in a.items():
                if key & i == k:
                    ans += value

                b[key & i] += value
            b[i] += 1

            a, b = b, a

        return ans
