class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int n = nums.size();

        unordered_map<long long, long long> andFreq;

        long long res = 0;
        for(long long i = 0;i<n;i++){
            unordered_map<long long, long long> temp;
            for(auto it: andFreq){
                long long x= it.first&(long long)nums[i];
                temp[x]+=it.second;
            }

            temp[nums[i]]++;
            andFreq = temp;

            if(andFreq.find(k) != andFreq.end()){
                res+=andFreq[k];
            }
        }

        return res;
    }
};
