class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0;
        Map<Long, Long> map = new HashMap<>();
        for(int i=0;i<nums.length;++i){
            if( nums[i]==k) count++;
            Map<Long,Long> curr = new HashMap<>();
            curr.put((long) nums[i], curr.getOrDefault((long) nums[i], 0L) + 1L);
             for (Map.Entry<Long, Long> entry : map.entrySet()){
              long x = entry.getKey();
             long y = entry.getValue();
             long and = x & nums[i];
              if(and ==k) count += y;
               curr.put(and, curr.getOrDefault(and, 0L) + y);
            }
             map = curr;
        }
        return count;
    }
}
