class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<int, long long> mp;
        for(int i = 0; i < n; i++) {
            if(nums[i] == k)
                ans++;
            unordered_map<int, long long> temp;
            temp[nums[i]]++;
            for(auto it : mp) {
                long long curr = it.first & nums[i];
                if(curr == k)
                    ans += it.second;
                temp[curr] += it.second;
            }
            mp = temp;            
        }
        return ans;
    }
};
