class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long total = 0;
        unordered_map<long long, long long> prevResult;
          for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> currResult; 
            if (nums[i] == k) {
                ++total;
            }
                currResult[nums[i]] = 1;
                for (auto& [value, frequency] : prevResult) {
                      long long newResult = value & nums[i];
                      if (newResult == k) {
                        total += frequency;
                }
                      currResult[newResult] += frequency;
            }
                prevResult = currResult;
          }
        return total;
    }
};
