class Solution {
public:
    long long countSubarrays(vector<int>& arr, int target) {
    int n = arr.size();
    long long totalCount = 0;

    std::unordered_map<long long, long long> previousAndResults;

    for (int i = 0; i < n; ++i) {
        std::unordered_map<long long, long long> currentAndResults; 

        if (arr[i] == target) {
            ++totalCount;
        }
        currentAndResults[arr[i]] = 1;

        for (const auto& [andResult, frequency] : previousAndResults) {
            long long newAndResult = andResult & arr[i];
            if (newAndResult == target) {
                totalCount += frequency;
            }
            currentAndResults[newAndResult] += frequency;
        }

        previousAndResults = currentAndResults;
    }

    return totalCount;
    }
};
