class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
    int size = array.size();
        long long total = 0;
        unordered_map<long long, long long> prev;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> curr; 
            if (array[index] == target) {
                ++total;
            }
            curr[array[index]] = 1;
            for (auto& [value, frequency] : prev) {
                long long newre = value & array[index];
                if (newre == target) {
                    total += frequency;
                }
                curr[newre] += frequency;
            }

            prev = curr;
        }
        return total;
    }
};
