class Solution {
public:
    long long countSubarrays(vector<int>& a, int b) {
        int n = a.size();
        long long ans = 0;
        unordered_map<long long, long long> pre;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cur;
            if (a[i] == b) {
                ans++;
            }
            cur[a[i]] = 1;

            updateCurrRes(pre, cur, a[i], b, ans);
            pre = cur;
        }

        return ans;
    }

private:
    void updateCurrRes(
        const unordered_map<long long, long long>& prev_res,
        unordered_map<long long, long long>& curr_res,
        int c,
        int b,
        long long& ans
    ) {
        for (const auto& [d, e] : prev_res) {
            long long f = d & c;
            if (f == b) {
               ans += e;
            }
            curr_res[f] += e;
        }
    }
};
