class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0; 
        unordered_map<long long, long long> map1;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> map2; 
            if (nums[i] == k) {
                ans++;
            }
            map2[nums[i]] = 1;
            for (auto& [val, freq] : map1) {
                long long temp = val & nums[i];
                if (temp == k) {
                    ans += freq;
                }
                map2[temp] += freq;
            }
            map1 = map2;
        }
        return ans;
    }
};
