class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        N = len(nums)
        mask = (1 << 32) - 1

        @cache
        def go(idx, acc):
            if idx == N or acc < k:
                return 0

            new_acc = acc & nums[idx]
            return go(idx + 1, new_acc) + (1 if new_acc == k else 0)

        total = 0
        for i in range(N):
            r = go(i, mask)
            total += r

        return total
