class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt=0;
        map<long long ,long long>mp;
        for(int i=0;i<nums.size();i++){
            map<long long ,long long>mp1;
            if(nums[i]==k){
                cnt++;
            }
            mp1[nums[i]]=1;
            for(auto it :mp){
                long long op =it.first & nums[i];
                if(op==k){
                    cnt+=it.second;
                }
                mp1[op]+=it.second;
            }
            mp=mp1;
        }
        return cnt;
    }
};
