class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long ans = 0;
        HashMap<Long, Long> hm = new HashMap<>();
        HashMap<Long, Long> curr = new HashMap<>();
        for(int i = 0; i < n; i++){
            curr.clear();
            if(nums[i]==k) ans++;
            curr.put((long)nums[i], curr.getOrDefault(nums[i], 0l)+1);
            for(Map.Entry<Long, Long> entry : hm.entrySet()){
                long flag = entry.getKey() & nums[i];
                if(flag == k) ans += entry.getValue();
                curr.put(flag, curr.getOrDefault(flag, 0l) + entry.getValue());
            }
            hm = new HashMap<>(curr);
        }
        return ans;
    }
}
