class Solution {
    private: 
    void helper(vector<int>&nums, int k, long long &cnt){
         unordered_map<long long , long long> ump;
         int n = nums.size();
        for(int i = 0 ; i < n; i++){
            unordered_map<long long, long long> um;

            if(nums[i] == k){
                ++cnt;
            }
            um[nums[i]] = 1;

            for(auto it : ump){
                long long ans = it.first & nums[i];
                if(ans == k){
                    cnt += it.second;
                }
                um[ans] += it.second;
            }
            ump = um;
        }
    }
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt  = 0;

        helper(nums, k , cnt);

        return cnt;
    }
};
