class Solution {
public:
    long long solve(vector<int> &nums, int k){
        long long ans = 0;
        int i = 0 , j = 0;
        int n= nums.size();
        vector<int> bits(32,0);
        int val = INT_MAX;
        while(j<n){
            val &= nums[j];
            for(int i =0;i<32;i++){
                if(!(nums[j]&(1<<i))) bits[i]++;
            }
            while(val < k && i<=j){
                for(int id = 0;id<32;id++){
                    if(!(nums[i]&(1<<id))) bits[id]--;
                    if(bits[id] == 0) val |= (1<<id);
                }
                i++;
            }
            ans += (j-i+1);
            j++;
        }
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums,k)-solve(nums,k+1);
    }
};
