class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<long long ,long long>mp1;
        long long ans=0;
        for(int i=0;i<n;i++){
            unordered_map<long long ,long long>mp2;
            int a=nums[i];
            if(a==k)ans++;
            mp2[nums[i]]++;
            for(auto it:mp1){
                long long b=it.first&a;
                if(b==k)ans+=it.second;
                mp2[b]+=it.second;
            }
            mp1=mp2;   
        }
        return ans;
    }
};
