#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        ll count=0;

        map<ll,ll>mp;
        for(ll i=0;i<nums.size();i++)
        {
            if(nums[i]==k)
            count++;

            map<ll,ll>next;
            next[nums[i]]++;

            for(auto it:mp)
            {
                ll res=(it.first&nums[i]);
                if(res==k)
                {
                    count+=it.second;

                }
                next[res]+=it.second;

            }
            mp=next;

        }
        return count;








    }

};
