class Solution {
public:
    int n;
    long long countSubarrays(vector<int>& nums, int k) {
        n=nums.size();
        long long result=0;
        unordered_map<long long,long long>pre;
        for(int i=0;i<n;i++){
              unordered_map<long long,long long>curr;
              if(nums[i]==k) result++;
              curr[nums[i]]=1;
              for(auto it:pre){
                long long ans=nums[i]&(it.first);
                if(ans==k) result+=it.second;
                curr[ans]+=it.second;
              }
              pre=curr;
        }
        return result;
    }
};
