class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {

        long long total = 0;
        unordered_map<long long, long long> prevResults;
        int size = array.size();


        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> Results;

            if (array[i] == target) 
                ++total;


            Results[array[i]] = 1;

            for (const auto& [val, freq] : prevResults) {
                long long andResult = val & array[i];
                if (andResult == target) {
                    total += freq;
                }
                Results[andResult] += freq;
            }

            prevResults = Results;
        }

        return total;
    }

};
