class Solution {
public:
    int find_val(vector<pair<int,int>>&mask){
        int n = mask.size();
        long long m = 1;
        int ans = 0;
        for(int i = 0 ; i < n ; i++){
            if(mask[i].first > 0 && mask[i].second <= 0){
                ans += m;
            }
            m *=2;
        }

        return ans;
    }
    long long solve(vector<int>&nums , int k){
        vector<pair<int,int>>mask(31,{0,0});
        int and_val = 0;
        int i = 0 , j = 0;
        int n = nums.size();
        long long ans = 0;
        while(j < n){
            for(int z = 0 ; z < 31 ; z++){
                if((nums[j] >> z) & 1) mask[z].first++;
                else mask[z].second++;
            }

            and_val = find_val(mask);
            while(i <= j && and_val <= k){
                for(int z = 0 ; z < 31 ; z++){
                    if((nums[i] >> z) & 1) mask[z].first--;
                    else mask[z].second--;
                }
                and_val = find_val(mask);
                i++;
            }

            ans += (j - i + 1);
            j++;

        }

        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums,k-1) - solve(nums,k);
    }
};
