class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long res = 0;
        unordered_map<long long, long long> mp;
        for (int i = 0; i< n; i++) {
            unordered_map<long long, long long> mpp; 
            if (nums[i] == k) {
                ++res;
            }
            mpp[nums[i]] = 1;
            for (auto& [value, frequency] : mp) {
                long long tot = value & nums[i];
                if (tot == k) {
                    res += frequency;
                }
                mpp[tot] += frequency;
            }

            mp = mpp;
        }
        return res;
    }
};
