class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long count=0;
       unordered_map<long long , long long>mp;
        for(int i=0;i<n;i++){
            unordered_map<long long , long long>curr;
            if(nums[i]==k){
                count++;
            }
            curr[nums[i]]=1;
            for(auto &it:mp){
                long long nres=it.first&nums[i];
                if(nres==k){
                    count+=it.second;
                }
                curr[nres]+=it.second;
            }
            mp=curr;
        }
        return count;
    }
};
