#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size();
        ll ans = 0;
        unordered_map<ll, ll> mp;
        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> mp2; 
            if (nums[i] == k) {
                ans++;
            }
            mp2[nums[i]] = 1;
            for (auto& [val, freq] : mp) {
                ll curr = val & nums[i];
                if (curr == k) {
                    ans += freq;
                }
                mp2[curr] += freq;
            }
            mp = mp2;
        }
        return ans;
    }
};
