class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> prev;

        for(int i=0; i<n; i++) {
            unordered_map<long long, long long> cur; 
            if (nums[i] == k) {
                ans++;
            }
            cur[nums[i]] = 1;
            for (auto& it: prev) {
                long long latest = it.first & nums[i];
                if (latest == k) {
                    ans += it.second;
                }
                cur[latest] += it.second;
            }
            prev = cur;
        }
        return ans;
    }
};
