#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n=nums.size();
        ll ans=0;
        map<ll,ll> prev;
        for(ll i=0;i<n;i++){
            map<ll,ll> get;
            if(nums[i]==k){
                ans++;
            }
            get[nums[i]]=1;
            for(auto it:prev){
                ll take=it.first & (ll)nums[i];
                if(take==k) ans+=it.second;
                get[take]+=it.second;
            }
            prev=get;

        }
        return ans;
    }
};
